    private static int bestFitness(Agent[] a) {
        double best = -1;
        int index = -1;
        for (int i = 0; i < a.length; i++) {
            if (a[i].getFitness() > best) {
                best = a[i].getFitness();
                index = i;
            }

        }
        return index;
    }
